use select::document::Document;
use select::predicate::{Attr, Name};

pub fn sandwirt_html_select_test() {
    let document = Document::from_read(html_example().as_bytes()).unwrap();

    for article in document.find(Name("article")) {
        let author =
            article
                .clone()
                .find(Attr("class", "post-author-name fn"))
                .next()
                .map(|node| node.text())
                .unwrap_or("Der Sandwirt".to_string())
                .trim()
                .to_owned();

        let title =
            article
                .clone()
                .find(Name("a"))
                .next()
                .map(|node| node.text())
                .unwrap_or("".to_string())
                .trim()
                .to_owned();

        let href =
            article
                .find(Name("a")).next()
                .and_then(|node| node.attr("href"))
                .unwrap_or("")
                .trim()
                .to_owned();

        println!("Author: {}", author);
        println!("Title: {}", title);
        println!("Href-Link: {}", href);
        println!("---");
    }
}

